 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
DECLARE
    payment_entity_id uuid;
    user_id_value uuid;
BEGIN
    IF (TG_OP = 'INSERT' OR TG_OP = 'UPDATE') THEN
        --  payment_id  payment_approvals
